import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { Icon, Label, Menu, Table, Button } from 'semantic-ui-react';
import ProductService from '../services/productService';
import {addToCart}  from "../store/actions/cartActions";

export default function ProductList() {

    const dispatch = useDispatch()
    const [products, setProducts] = useState([]);
    
    useEffect(()=>{
        let productService = new ProductService()
        productService.getProducts().then(result=>setProducts(result.data.data))
    },[])

    const handleAddToCart=(product)=>{
        dispatch(addToCart(product))
    }

    return (
        <div>
            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Ürün Adı</Table.HeaderCell>
                        <Table.HeaderCell>Birim Fiyatı</Table.HeaderCell>
                        <Table.HeaderCell>Stok Adedi</Table.HeaderCell>
                        <Table.HeaderCell>Açıklama</Table.HeaderCell>
                        <Table.HeaderCell>Kategori</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {
                        products.map((products) => (
                            <Table.Row key={products.id}> 
                                <Table.Cell><Link to={`/products/${products.productName}`}>{products.productName}</Link></Table.Cell>
                                <Table.Cell>{products.unitPrice}</Table.Cell>
                                <Table.Cell>{products.unitsInStock}</Table.Cell>
                                <Table.Cell>{products.quantityPerUnit}</Table.Cell>
                                <Table.Cell>{products.category.categoryName}</Table.Cell>
                                <Table.HeaderCell><Button onClick={handleAddToCart(product)}></Button></Table.HeaderCell>
                            </Table.Row>
                        ))
                    }


                </Table.Body>

                <Table.Footer>
                    <Table.Row>
                        <Table.HeaderCell colSpan='3'>
                            <Menu floated='right' pagination>
                                <Menu.Item as='a' icon>
                                    <Icon name='chevron left' />
                                </Menu.Item>
                                <Menu.Item as='a'>1</Menu.Item>
                                <Menu.Item as='a'>2</Menu.Item>
                                <Menu.Item as='a'>3</Menu.Item>
                                <Menu.Item as='a'>4</Menu.Item>
                                <Menu.Item as='a' icon>
                                    <Icon name='chevron right' />
                                </Menu.Item>
                            </Menu>
                        </Table.HeaderCell>
                    </Table.Row>
                </Table.Footer>
            </Table>
        </div>
    )
}
